import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useNavigate, useParams } from 'react-router-dom'; // Importa useNavigate
import 'bootstrap/dist/css/bootstrap.min.css';

const EditCategory = () => {
    const { id } = useParams(); // Obtiene el ID de la categoría de la URL
    const navigate = useNavigate(); // Obtiene la función navigate
    const [category, setCategory] = useState({}); // Estado para almacenar los detalles de la categoría

    useEffect(() => {
        // Hace una solicitud GET para obtener los detalles de la categoría
        axios.get(`http://localhost:8090/eva04daw/categories/editarCategorie/${id}`)
        .then(response => setCategory(response.data))
        .catch(error => console.error('Error fetching category', error));
    }, [id]);

    const handleInputChange = (event) => {
        // Actualiza el estado de la categoría cuando el usuario edita el formulario
        setCategory({
            ...category,
            [event.target.name]: event.target.value
        });
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        // Hace una solicitud POST o PUT para actualizar la categoría
        axios.post(`http://localhost:8090/eva04daw/categories/actualizarCategorie/${id}`, category)
        .then(response => {
            console.log('Category updated', response);
            navigate('/'); // Redirige al usuario a la página principal
        })
        .catch(error => console.error('Error updating category', error));
    };

    return (
        <div className="container mt-4">
            <form onSubmit={handleSubmit}>
                {/* Aquí van tus campos de formulario, por ejemplo: */}
                <div className="mb-3">
                    <label htmlFor="name" className="form-label">Nombre</label>
                    <input type="text" className="form-control" id="name" name="name" value={category.name} onChange={handleInputChange} />
                </div>
                {/* Asegúrate de incluir todos los campos que la categoría pueda tener */}
                <button type="submit" className="btn btn-primary">Actualizar categoría</button>
            </form>
        </div>
    );
};

export default EditCategory;
