import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useNavigate, useParams } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

const EditProducto = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [producto, setProducto] = useState({});

    useEffect(() => {
        axios.get(`http://localhost:8090/eva04daw/products/getById/${id}`)
        .then(response => setProducto(response.data))
        .catch(error => console.error('Error fetching producto', error));
    }, [id]);

    const handleInputChange = (event) => {
        setProducto({
            ...producto,
            [event.target.name]: event.target.value
        });
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        axios.put(`http://localhost:8090/eva04daw/products/editarCategorie/${id}`, producto)
        .then(response => {
            console.log('producto updated', response);
            navigate('/');
        })
        .catch(error => console.error('Error updating producto', error));
    };

    return (
        <div className="container mt-4">
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label htmlFor="id" className="form-label">ID</label>
                    <input type="text" className="form-control" id="id" name="id" value={producto.id} onChange={handleInputChange} disabled />
                </div>
                <div className="mb-3">
                    <label htmlFor="name" className="form-label">Nombre</label>
                    <input type="text" className="form-control" id="name" name="name" value={producto.name} onChange={handleInputChange} />
                </div>
                <div className="mb-3">
                    <label htmlFor="description" className="form-label">Descripción</label>
                    <input type="text" className="form-control" id="description" name="description" value={producto.description} onChange={handleInputChange} />
                </div>
                <div className="mb-3">
                    <label htmlFor="status" className="form-label">Estado</label>
                    <input type="text" className="form-control" id="status" name="status" value={producto.status} onChange={handleInputChange} />
                </div>
                <div className="mb-3">
                    <label htmlFor="createdat" className="form-label">Creado en</label>
                    <input type="text" className="form-control" id="createdat" name="createdat" value={producto.createdat} onChange={handleInputChange} disabled />
                </div>
                <div className="mb-3">
                    <label htmlFor="updatedat" className="form-label">Actualizado en</label>
                    <input type="date" className="form-control" id="updatedat" name="updatedat" value={producto.updatedat} onChange={handleInputChange} />
                </div>
                <button type="submit" className="btn btn-primary">Actualizar categoría</button>
            </form>
            <Link to="/" className="btn btn-secondary mt-2">Cancelar</Link>
        </div>
    );
};

export default EditProducto;
