import React, { useEffect, useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link, useNavigate } from 'react-router-dom'; // Importa useNavigate

const CategoryList = () => {
    const [categories, setCategories] = useState([]);
    const navigate = useNavigate(); // Obtiene la función navigate

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = () => {
        axios.get('http://localhost:8090/eva04daw/categories/getAll')
        .then(response => setCategories(response.data))
        .catch(error => console.error('Error fetching categories', error));
    };

    useEffect(() => {
        axios.get('http://localhost:8090/eva04daw/products/getAll')
        .then(response => setProducts(response.data))
        .catch(error => console.error('Error fetching products', error));
    }, []);

    const deleteCategory = (id) => {
        axios.put(`http://localhost:8090/eva04daw/categories/eliminarCategorie/${id}`)
        .then(response => {
            console.log('Category deleted', response);
            fetchData();
        })
        .catch(error => console.error('Error deleting category', error));
    };

    return (
        <div className="container mt-4">
            <Link to="/create" className="btn btn-primary">Agregar categoría</Link>

            <table className="table mt-4">
                <thead>
                <tr>
                    <th>ID</th>
                    <th>Name</th>
                    <th>Description</th>
                    <th>Status</th>
                    <th>Created At</th>
                    <th>Updated At</th>
                    <th>Acciones</th> 
                </tr>
                </thead>
                <tbody>
                {categories.map(category => (
                    <tr key={category.id}>
                    <td>{category.id}</td>
                    <td>{category.name}</td>
                    <td>{category.description}</td>
                    <td>{category.status}</td>
                    <td>{category.createdat}</td>
                    <td>{category.updatedat}</td>
                    <td>
                        <button className="btn btn-warning" onClick={() => navigate(`/edit/${category.id}`)}>Editar</button> {/* Botón para editar */}
                        <button className="btn btn-danger" onClick={() => deleteCategory(category.id)}>Eliminar</button> {/* Botón para eliminar */}
                    </td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
};

export default CategoryList;
