import React, { useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom'; 
import 'bootstrap/dist/css/bootstrap.min.css';

const CreateProducto = () => {
    const [newProducto, setNewProducto] = useState({});
    const navigate = useNavigate(); 

    const createProducto = (event) => {
        event.preventDefault();
        axios.post('http://localhost:8090/eva04daw/products/crearProduct', newProducto)
        .then(response => {
            console.log('Producto created', response);
            navigate('/products');
        })
        .catch(error => console.error('Error creating Producto', error));
    };

    const handleInputChange = (event) => {
        setNewProducto({...newProducto, [event.target.name]: event.target.value});
    };

    return (
        <div className="container mt-4">
    <form onSubmit={createProducto}>

        <div className="mb-3">
            <label htmlFor="name" className="form-label">Nombre del Producto</label>
            <input type="text" className="form-control" name="name" onChange={handleInputChange} placeholder="Nombre del producto" required />
        </div>

        <div className="mb-3">
            <label htmlFor="description" className="form-label">Descripción de la producto</label>
            <input type="text" className="form-control" name="description" onChange={handleInputChange} placeholder="Descripción de la producto" required />
        </div>

        <div className="mb-3">
            <label htmlFor="status" className="form-label">Estado de la producto</label>
            <input type="number" className="form-control" min="0" max="1" name="status" onChange={handleInputChange} placeholder="Estado de la producto" required />
        </div>

        <div className="mb-3">
            <label htmlFor="categoryid" className="form-label">Categoria de la producto</label>
            <input type="number" className="form-control" min="0" max="1" name="categoryid" onChange={handleInputChange} placeholder="Estado de la producto" required />
        </div>

        <div className="mb-3">
            <label htmlFor="details" className="form-label">Detalles de la producto</label>
            <input type="text" className="form-control" name="details" onChange={handleInputChange} placeholder="Descripción de la producto" required />
        </div>

        <div className="mb-3">
            <label htmlFor="createdat" className="form-label">Fecha de creación</label>
            <input type="date" className="form-control" name="createdat" onChange={handleInputChange} required />
        </div>

        <div className="mb-3">
            <label htmlFor="updatedat" className="form-label">Fecha de actualización</label>
            <input type="date" className="form-control" name="updatedat" onChange={handleInputChange} required />
        </div>
        <button type="submit" className="btn btn-primary">Crear categoría</button>

    </form>

    <Link to="/" className="btn btn-secondary mt-2">Cancelar</Link>
</div>

    );
};

export default CreateProducto;
