import React, { useEffect, useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

const CategoryList = () => {
    const [categories, setCategories] = useState([]);
    const [newCategory, setNewCategory] = useState({});

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = () => {
        axios.get('http://localhost:8090/eva04daw/categories/getAll')
        .then(response => setCategories(response.data))
        .catch(error => console.error('Error fetching categories', error));
    };

    const createCategory = (event) => {
        event.preventDefault();
        axios.post('http://localhost:8090/eva04daw/categories/crearCategorie', newCategory)
        .then(response => {
            console.log('Category created', response);
            // Actualiza la lista de categorías después de crear una nueva categoría
            fetchData();
        })
        .catch(error => console.error('Error creating category', error));
    };

    const handleInputChange = (event) => {
        setNewCategory({...newCategory, [event.target.name]: event.target.value});
    };

    return (
        <div className="container mt-4">
            <form onSubmit={createCategory}>
                <input type="text" name="name" onChange={handleInputChange} placeholder="Nombre de la categoría" required />
                <input type="text" name="description" onChange={handleInputChange} placeholder="Descripción de la categoría" required />
                <div className="mb-3">
                    <label htmlFor="status" className="form-label">Status</label>
                    <select className="form-select"  name="status" onChange={handleInputChange}>
                        <option value={1}>Active</option>
                        <option value={0}>Inactive</option>
                    </select>
                </div>
                <button type="submit">Crear categoría</button>
            </form>

            <table className="table mt-4">
                <thead>
                <tr>
                    <th>ID</th>
                    <th>Name</th>
                    <th>Description</th>
                    <th>Status</th>
                    <th>Created At</th>
                    <th>Updated At</th>
                </tr>
                </thead>
                <tbody>
                {categories.map(category => (
                    <tr key={category.id}>
                    <td>{category.id}</td>
                    <td>{category.name}</td>
                    <td>{category.description}</td>
                    <td>{category.status}</td>
                    <td>{category.createdat}</td>
                    <td>{category.updatedat}</td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
};

export default CategoryList;
