import React, { useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom'; // Importa useNavigate
import 'bootstrap/dist/css/bootstrap.min.css';

const CreateCategory = () => {
    const [newCategory, setNewCategory] = useState({});
    const navigate = useNavigate();

    const createCategory = (event) => {
        event.preventDefault();
        axios.post('http://localhost:8090/eva04daw/categories/crearCategorie', newCategory)
        .then(response => {
            console.log('Category created', response);
            navigate('/categories'); 
        })
        .catch(error => console.error('Error creating category', error));
    };

    const handleInputChange = (event) => {
        setNewCategory({...newCategory, [event.target.name]: event.target.value});
    };

    return (
        <div className="container mt-4">
    <form onSubmit={createCategory}>

        {/* Nombre de la categoría */}
        <div className="mb-3">
            <label htmlFor="name" className="form-label">Nombre de la categoría</label>
            <input type="text" className="form-control" name="name" onChange={handleInputChange} placeholder="Nombre de la categoría" required />
        </div>

        {/* Descripción de la categoría */}
        <div className="mb-3">
            <label htmlFor="description" className="form-label">Descripción de la categoría</label>
            <input type="text" className="form-control" name="description" onChange={handleInputChange} placeholder="Descripción de la categoría" required />
        </div>

        {/* Estado de la categoría */}
        <div className="mb-3">
            <label htmlFor="status" className="form-label">Estado de la categoría</label>
            <input type="number" className="form-control" min="0" max="1" name="status" onChange={handleInputChange} placeholder="Estado de la categoría" required />
        </div>

        {/* Fecha de creación */}
        <div className="mb-3">
            <label htmlFor="createdat" className="form-label">Fecha de creación</label>
            <input type="date" className="form-control" name="createdat" onChange={handleInputChange} required />
        </div>

        {/* Fecha de actualización */}
        <div className="mb-3">
            <label htmlFor="updatedat" className="form-label">Fecha de actualización</label>
            <input type="date" className="form-control" name="updatedat" onChange={handleInputChange} required />
        </div>

        {/* Botón de enviar */}
        <button type="submit" className="btn btn-primary">Crear categoría</button>

    </form>

    {/* Enlace de cancelar */}
    <Link to="/" className="btn btn-secondary mt-2">Cancelar</Link>
</div>

    );
};

export default CreateCategory;
