import React, { useState } from 'react';
import axios from 'axios';

const CategoryForm = () => {
    const [formData, setFormData] = useState({
        name: '',
        description: '',
        status: 1, // Puedes ajustar el valor predeterminado según tus necesidades
        // Otros campos del modelo
    });

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post('http://localhost:8090/eva04daw/categories/crearCategorie', formData, {
                withCredentials: true,
                headers: {
                    'Content-Type': 'application/json',
                },
            });
            console.log('Category created successfully');
            // Puedes agregar lógica adicional después de crear la categoría, como redireccionar a la lista de categorías, etc.
        } catch (error) {
            console.error('Error creating category', error);
        }
    };

    return (
        <div>
            <h2>Create Category</h2>
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label htmlFor="name" className="form-label">Name</label>
                    <input type="text" className="form-control" id="name" name="name" value={formData.name} onChange={handleChange} />
                </div>
                <div className="mb-3">
                    <label htmlFor="description" className="form-label">Description</label>
                    <textarea className="form-control" id="description" name="description" value={formData.description} onChange={handleChange}></textarea>
                </div>
                <div className="mb-3">
                    <label htmlFor="status" className="form-label">Status</label>
                    <select className="form-select" id="status" name="status" value={formData.status} onChange={handleChange}>
                        <option value={1}>Active</option>
                        <option value={0}>Inactive</option>
                    </select>
                </div>
                {/* Agrega otros campos del modelo aquí */}
                <button type="submit" className="btn btn-primary">Create Category</button>
            </form>
        </div>
    );
};

export default CategoryForm;
