import React, { useEffect, useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link, useNavigate } from 'react-router-dom';

const ProductList = () => {
const [products, setProducts] = useState([]);
const navigate = useNavigate(); 


useEffect(() => {
    fetchData();
}, []);

const fetchData = () => {
    axios.get('http://localhost:8090/eva04daw/products/getAll')
    .then(response => setProducts(response.data))
    .catch(error => console.error('Error fetching products', error));
};

const deleteProducto = (id) => {
    axios.put(`http://localhost:8090/eva04daw/products/eliminarProduct/${id}`)
    .then(response => {
        console.log('products deleted', response);
        fetchData();
    })
    .catch(error => console.error('Error deleting products', error));
};

return (
    <div className="container mt-4">
    <Link to="/createProducto" className="btn btn-primary">Agregar Producto</Link>
    <table className="table mt-4">
        <thead>
        <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Description</th>
            <th>Details</th>
            <th>Category ID</th>
            <th>Status</th>
            <th>Created At</th>
            <th>Updated At</th>
            <th>Acciones</th> 
        </tr>
        </thead>
        <tbody>
        {products.map(product => (
            <tr key={product.id}>
            <td>{product.id}</td>
            <td>{product.name}</td>
            <td>{product.description}</td>
            <td>{product.details}</td>
            <td>{product.categoryid}</td>
            <td>{product.status}</td>
            <td>{product.createdat}</td>
            <td>{product.updatedat}</td>
            <td>
                <button className="btn btn-warning" onClick={() => navigate(`/editProducto/${product.id}`)}>Editar</button> <br></br>
                <button className="btn btn-danger" onClick={() => deleteProducto(product.id)}>Eliminar</button> {/* Bot√≥n para eliminar */}
            </td>
            </tr>
        ))}
        </tbody>
    </table>
    </div>
);
};

export default ProductList;
