import React, { useState } from 'react';
import axios from 'axios';

const CategoryForm = ({ onCategoryCreated }) => {
  const [categoryData, setCategoryData] = useState({
    name: '',
    description: '',
    status: 1, // Puedes establecer un valor predeterminado para el estado
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCategoryData({ ...categoryData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    axios.post('http://localhost:8090/eva04daw/categories/crearCategorie', categoryData)
      .then(response => {
        // Si la categoría se crea con éxito, llama a la función proporcionada para actualizar la lista
        onCategoryCreated();
        // Limpia el formulario
        setCategoryData({
          name: '',
          description: '',
          status: 1,
        });
      })
      .catch(error => console.error('Error creating category', error));
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="mb-3">
        <label htmlFor="name" className="form-label">Name:</label>
        <input
          type="text"
          className="form-control"
          id="name"
          name="name"
          value={categoryData.name}
          onChange={handleInputChange}
          required
        />
      </div>
      <div className="mb-3">
        <label htmlFor="description" className="form-label">Description:</label>
        <input
          type="text"
          className="form-control"
          id="description"
          name="description"
          value={categoryData.description}
          onChange={handleInputChange}
          required
        />
      </div>
      <div className="mb-3">
        <label htmlFor="status" className="form-label">Status:</label>
        <select
          className="form-control"
          id="status"
          name="status"
          value={categoryData.status}
          onChange={handleInputChange}
          required
        >
          <option value={1}>Active</option>
          <option value={0}>Inactive</option>
        </select>
      </div>
      <button type="submit" className="btn btn-primary">Create Category</button>
    </form>
  );
};

export default CategoryForm;
