            })
            .catch(error => console.error('Error fetching cursos', error));
    };

    return (
        <div className="container mt-4">
            <table className="table mt-4">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Titulo</th>
                        <th>Descripcion</th>
                        <th>Semestre</th>
                    </tr>
                </thead>
                <tbody>
                    {cursos.map(curso => (
                        <tr key={curso.id}>
                            <td>{curso.id}</td>
                            <td>{curso.titulo}</td>
                            <td>{curso.descripcion}</td>
                            <td>{curso.semestre}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default SegundoSemestreList;
