import React, { useState } from 'react';
import './../css/ComprarPlantas.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Navbar from './NavBar';
import Fotter from './Fotter';

function PreguntasFrecuentes() {
    const [minPrecio, setMinPrecio] = useState(0);
    const [maxPrecio, setMaxPrecio] = useState(500);

    const handleMinPrecioChange = (event) => {
        setMinPrecio(parseInt(event.target.value));
    };

    const handleMaxPrecioChange = (event) => {
        setMaxPrecio(parseInt(event.target.value));
    };

    return (
        <div className="contenedor_compra_plantas">
            <Navbar />
            <div className="contenedor_filtros_secciones">
                <div className="contenedor_titulo_filtros">
                    <div className="contenedor_titulo_filtros_content">
                        <div className="titulo_filtros">Filtrar</div>
                        <div className="contenedor_filtros">
                            <div className="contenedor_filtros_precio">
                                <div className="contenedor_filtros_precio_titulo">Precio</div>
                                <input
                                    type="range"
                                    min="0"
                                    max="500"
                                    step="1"
                                    value={minPrecio}
                                    onChange={handleMinPrecioChange}
                                />
                                <input
                                    type="range"
                                    min="0"
                                    max="500"
                                    step="1"
                                    value={maxPrecio}
                                    onChange={handleMaxPrecioChange}
                                />
                                <div className="contenedor_filtros_precio_ranges">
                                    <div>S/.{minPrecio}</div>
                                    <div>S/.{maxPrecio}</div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="contenedor_secciones">
                    {/* Aqu√≠ va el contenido de las secciones */}
                </div>
            </div>
            <Fotter />
        </div>
    );
}

export default PreguntasFrecuentes;
