import React, { useState } from 'react';
import axios from 'axios';
import { Link, useHistory } from 'react-router-dom'; // Importa useHistory
import 'bootstrap/dist/css/bootstrap.min.css';

const CreateCategory = () => {
    const [newCategory, setNewCategory] = useState({});
    const history = useHistory(); // Obtiene el objeto history

    const createCategory = (event) => {
        event.preventDefault();
        axios.post('http://localhost:8090/eva04daw/categories/crearCategorie', newCategory)
        .then(response => {
            console.log('Category created', response);
            history.push('/categories'); // Redirige a /categories
        })
        .catch(error => console.error('Error creating category', error));
    };

    const handleInputChange = (event) => {
        setNewCategory({...newCategory, [event.target.name]: event.target.value});
    };

    return (
        <div className="container mt-4">
            <form onSubmit={createCategory}>
                <input type="text" name="name" onChange={handleInputChange} placeholder="Nombre de la categoría" required />
                <input type="text" name="description" onChange={handleInputChange} placeholder="Descripción de la categoría" required />
                <input type="number" min="0" max="1" name="status" onChange={handleInputChange} placeholder="Estado de la categoría" required />
                <input type="date" name="createdat" onChange={handleInputChange} required />
                <input type="date" name="updatedat" onChange={handleInputChange} required />
                <button type="submit">Crear categoría</button>
            </form>
            <Link to="/">Cancelar</Link>
        </div>
    );
};

export default CreateCategory;
